"""Module containing the ``DataFrame`` class."""
from ansys.dpf.post.errors import PandasImportError


class DataFrame:
    """Exposes the fields container generated by a result provider."""

    def __init__(
        self, fields_container=None, server=None, mesh_scoping=None, columns=None
    ):
        """Wrap a FieldsContainer within a DataFrame.

        Parameters
        ----------
        fields_container:
            :class:`ansys.dpf.core.fields_container.FieldsContainer`to wrap.
        server:
            DPF server to use.
        mesh_scoping:
            Scoping to use.
        columns:
            Columns to use.
        """
        self._fc = fields_container
        if columns:
            self._columns = columns

        if mesh_scoping:
            self._mesh_scoping = mesh_scoping

        # super().__init__(fields_container._internal_obj, server)

    def __len__(self):
        """Return the length of the DataFrame."""
        return len(self._fc)

    def __min__(self, **kwargs):
        """Return the minimum of the data."""
        return self.to_numpy().min()

    def __max__(self, **kwargs):
        """Return the maximum of the data."""
        return self.to_numpy().max()

    def max(self, **kwargs):
        """Return the maximum of the data."""
        return float(self.to_numpy().max())

    def min(self, **kwargs):
        """Return the minimum of the data."""
        return float(self.to_numpy().min())

    def to_pandas(self, columns=None, **kwargs):
        """Returns the current DPF DataFrame as a Pandas DataFrame.

        Parameters
        ----------
        columns :
            Columns description.

        Returns
        -------
        class:`pandas.core.frame.DataFrame`

        Examples
        --------
        >>> import pandas as pd
        >>> from ansys.dpf import post
        >>> from ansys.dpf.post import examples
        >>> simulation = post.load_simulation(examples.multishells_rst)
        >>> # Export the displacements vector field at step 1 as a DataFrame
        >>> displacement = simulation.displacement(steps=[1], nodes=[1, 2, 3])
        >>> df = displacement.to_pandas()
        >>> print(df)
                  X          Y         Z
        1  0.398320 -13.797378 -0.163767
        2  0.233114 -13.797652 -0.153190
        3  0.367542 -13.808151 -0.163739
        >>> print(df.name)
        displacement_1.s

        """
        try:
            import pandas as pd
        except ModuleNotFoundError:
            raise PandasImportError

        if not columns:
            columns = self._columns
        # The wrapped FieldsContainer should only contain one Field
        if len(self) > 1:
            raise NotImplementedError(
                "This function requires for the DataFrame to contain only"
                "one data entity."
            )
        df = pd.DataFrame(
            self.to_numpy(), columns=columns, index=self._mesh_scoping.ids
        )
        df.name = self._fc[-1].name
        return df

    def to_numpy(self):
        """Return the current DPF DataFrame as a NumPy ndarray.

        Returns
        -------
        class:`numpy.ndarray`

        Examples
        --------
        >>> from ansys.dpf import post
        >>> from ansys.dpf.post import examples
        >>> simulation = post.load_simulation(examples.multishells_rst)
        >>> # Export the displacements vector field at step 1 as a numpy.ndarray
        >>> displacement = simulation.displacement(steps=[1], nodes=[1, 2, 3])
        >>> arr = displacement.to_numpy()
        >>> print(arr)
        [[  0.39831985 -13.79737819  -0.16376683]
         [  0.23311378 -13.79765179  -0.15318972]
         [  0.36754181 -13.80815135  -0.16373945]]

        """
        return self._fc[-1].data

    def plot(self, **kwargs):
        """Plot the result."""
        self._fc[-1].plot(**kwargs)

    def animate(self, **kwargs):
        """Animate the result.

        Returns
        -------
            The interactive plotter object used for animation.
        """
        return self._fc.animate(**kwargs)
